const Signup = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [input, setInput] = useState({
    name: "",
    email: "",
    password: ""
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setInput((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }));
    // Clear error when user starts typing
    if (error) setError("");
  };

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const sendRequest = async () => {
    try {
      const res = await axios.post(`${serverURL}/api/user/signup`, {
        name: input.name,
        email: input.email,
        password: input.password
      });
      const data = res.data;
      console.log(data);
      return data;
    } catch (err) {
      console.log(err);
      const errorMessage = err.response?.data?.message || "Something went wrong";
      setError(errorMessage);
      Swal("Error", errorMessage, "error");
      throw err;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Signup form submitted', input);

    // Basic validation
    if (!input.name || !input.email || !input.password) {
      setError("Please fill in all fields");
      return;
    }

    setIsLoading(true);
    setError("");

    try {
      const data = await sendRequest();
      if (data && data.user) {
        localStorage.setItem("userId", data.user._id);
        dispatch(authActions.login(data.user));
        navigate("/blogs");
        Swal("Success", "Signup successful", "success");
      }
    } catch (err) {
      console.log(err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <AuthContainer maxWidth={false}>
      <Suspense fallback={<Loading />}>
        <AuthCard elevation={0} className="bounce-in">
          {/* Header */}
          <Box textAlign="center" mb={5}>
            <AuthTitle variant="h1">
              Join BLOGGY
            </AuthTitle>
            <AuthSubtitle variant="body1">
              Create your account and start sharing your stories with the world
            </AuthSubtitle>
          </Box>

          {/* Error Alert */}
          {error && (
            <Alert severity="error" sx={{ mb: 4, borderRadius: 3, fontFamily: "'Plus Jakarta Sans', sans-serif" }} className="slide-up">
              {error}
            </Alert>
          )}

          {/* Signup Form */}
          <form onSubmit={handleSubmit}>
            <StyledTextField
              required
              name="name"
              type="text"
              onChange={handleChange}
              value={input.name}
              placeholder="Enter your full name"
              fullWidth
              className="slide-up"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <PersonIcon color="action" sx={{ fontSize: '1.5rem' }} />
                  </InputAdornment>
                ),
              }}
            />

            <StyledTextField
              required
              name="email"
              type="email"
              onChange={handleChange}
              value={input.email}
              placeholder="Enter your email address"
              fullWidth
              className="slide-up"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EmailIcon color="action" sx={{ fontSize: '1.5rem' }} />
                  </InputAdornment>
                ),
              }}
            />

            <StyledTextField
              required
              name="password"
              type={showPassword ? "text" : "password"}
              onChange={handleChange}
              value={input.password}
              placeholder="Create a strong password"
              fullWidth
              className="slide-up"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <LockIcon color="action" sx={{ fontSize: '1.5rem' }} />
                  </InputAdornment>
                ),
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      onClick={handleTogglePassword}
                      edge="end"
                      size="large"
                      sx={{ 
                        color: 'action.active',
                        '&:hover': { 
                          color: 'primary.main',
                          transform: 'scale(1.1)',
                        },
                        transition: 'all 0.3s ease',
                      }}
                    >
                      {showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />

            <SignupButton
              type="submit"
              variant="contained"
              disabled={isLoading}
              startIcon={isLoading ? null : <PersonAddIcon />}
              className="slide-up"
            >
              {isLoading ? "Creating Account..." : "Create Account"}
            </SignupButton>
          </form>

          {/* Divider */}
          <Divider sx={{ my: 4 }} className="fade-in">
            <Typography variant="body2" color="text.secondary" fontFamily="'Plus Jakarta Sans', sans-serif" fontWeight="600">
              Already have an account?
            </Typography>
          </Divider>

          {/* Login Link */}
          <LoginLinkButton
            onClick={() => {
              console.log('Navigating to auth');
              navigate('/auth');
            }}
            startIcon={<ArrowForwardIcon />}
            className="slide-up"
          >
            Sign In Instead
          </LoginLinkButton>

          {/* Footer */}
          <Box mt={5} textAlign="center">
            <Typography variant="caption" color="text.secondary" fontFamily="'Plus Jakarta Sans', sans-serif" sx={{ opacity: 0.8 }}>
              By creating an account, you agree to our Terms of Service and Privacy Policy
            </Typography>
          </Box>
        </AuthCard>
      </Suspense>
    </AuthContainer>
  );
};

export default Signup;
